package loopsDemo;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		for(int i=0; i<=10; i++) {
			System.out.println(i);
		}
		

		
		for (int i=1; i<=5; i++) {
			System.out.println(i);
		}
	
		
		for (int i=0; i<=100; i+=25) {
			System.out.println(i);
		}
		
			
		for (int i=0; i<=6; i++) {
			if (i%2==0) {
				System.out.println(i);
			}
		}
		
		
	}

}
